import React, { useEffect, useState } from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import PopupWithForm from "./PopupWithForm";

function EditProfilePopup({ isOpen, onClose, onUpdateUser, onValidation }) {
    const currentUser = React.useContext(CurrentUserContext);
    React.useEffect(() => {
        setName(currentUser.name);
        setDescription(currentUser.about);
    }, [currentUser]);

    const [name, setName] = useState('');
    const [nameError, setNameError] = useState('');
    const [inputNameClassName, setInputNameClassName] = useState('');
    function handleNameChange(e) {
        setName(e.target.value);
        onValidation(e.target, setNameError, setInputNameClassName);
    }

    const [description, setDescription] = useState('');
    const [descriptionError, setDescriptionError] = useState('');
    const [inputDescriptionClassName, setInputDescriptionClassName] = useState('');
    function handleDescriptionChange(e) {
        setDescription(e.target.value);
        onValidation(e.target, setDescriptionError, setInputDescriptionClassName);
    }

    const [isFormValid, setFormValid] = useState(false);
    const [buttonClassName, setButtonClassName] = useState('button_inactive');
    useEffect(() => {
        if(nameError || descriptionError) {
            setFormValid(false);
            setButtonClassName('button_inactive');
        } else {
            setFormValid(false);
            setButtonClassName();
        }
    }, [nameError, descriptionError]);

    function clearInputErrors() {
        setName(currentUser.name);
        setNameError('');
        setInputNameClassName('');
        setDescription(currentUser.about);
        setDescriptionError('');
        setInputDescriptionClassName('');
    }

    function handleSubmit(e) {
        e.preventDefault();
        onUpdateUser({
          name: name,
          about: description
        });
        clearInputErrors();
    }

    function handleClose() {
        onClose();
        clearInputErrors();
    }

    return (
        <PopupWithForm
            name="edit-profile"
            title="Редактировать профиль"
            formName="editProfileForm"
            buttonText="Сохранить"
            isOpen={isOpen}
            onClose={handleClose}
            onSubmit={handleSubmit}
            isFormValid={isFormValid}
            buttonClassName={buttonClassName}
        >
            <label className="form__item">
                <input 
                    className="form__input form__input_type_name"
                    id="name-input"
                    type="text"
                    name="name"
                    placeholder="Ваше имя"
                    minLength="2"
                    maxLength="40"
                    required
                    value={name || ''}
                    onChange={handleNameChange}
                />
                <span className={`form__input-error name-input-error ${inputNameClassName}`}>{nameError}</span>
            </label>
            <label className="form__item">
                <input
                    className="form__input form__input_type_job"
                    id="job-input"
                    type="text"
                    name="about"
                    placeholder="Кратко о Вас"
                    minLength="2"
                    maxLength="200"
                    required
                    value={description || ''}
                    onChange={handleDescriptionChange}
                />
                <span className={`form__input-error job-input-error ${inputDescriptionClassName}`}>{descriptionError}</span>
            </label>
        </PopupWithForm>
    )
}

export default EditProfilePopup;
