import React from 'react';

class PopupWithForm extends React.Component {
    render() {
        return (
            <div className={`popup popup_type_${this.props.name}` (this.props.isOpen) ? '' : 'popup_is-opened'}>
          <div className="popup__container">
            <h2 className="popup__title">{this.props.title}</h2>
            <form className="form" name={this.props.name} novalidate>
              {this.props.children}
              <button className="button button_type_submit" type="submit">{this.props.buttonText}</button>
            </form>
            <button className="popup__close-btn"></button>
          </div>
        </div>
        )
    }
}

export default PopupWithForm;
