import React from 'react';

function handleEditAvatarClick() {
    document.querySelector('.popup_type_avatar-edit').classList.add('popup_is-opened');
}

function handleEditProfileClick() {
    document.querySelector('.popup_type_edit-profile').classList.add('popup_is-opened');
}

function handleAddPlaceClick() {
    document.querySelector('.popup_type_add-photo').classList.add('popup_is-opened');
}

function Main() {
    return (
        <main class="content">
          <section className="profile">
            <div className="profile__avatar">
              <img className="profile__avatar-image" src="<%=require('./images/avatar.jpg')%>" alt="фото профиля" />
              <div className="profile__darkening"></div>
              <div className="profile__avatar-edit-btn" onClick={handleEditAvatarClick}></div>
            </div>
            <div className="profile__info">
              <div className="profile__info-user">
                <h1 className="profile__name">Жак-Ив Кусто</h1>
                <button className="profile__edit-btn" onClick={handleEditProfileClick}></button>
              </div>
              <p className="profile__job">Исследователь океана</p>
            </div>
            <button className="profile__add-btn" onClick={handleAddPlaceClick}></button>
          </section>

          <section className="elements">
            <ul className="elements__list"></ul>
          </section>
        </main>
    )
}

export default Main;
