import React, { useState, useRef } from 'react';
import PopupWithForm from "./PopupWithForm";

function EditAvatarPopup({ isOpen, onClose, onUpdateAvatar, onValidation }) {
    const [avatar, setAvatar] = useState('');
    const [avatarError, setAvatarError] = useState('Вы пропустили это поле.');
    const [inputAvatarClassName, setInputAvatarClassName] = useState('');
    function handleAvatarChange(e) {
        setAvatar(e.target.value);
        onValidation(e.target, setAvatarError, setInputAvatarClassName);
    }

    const inputRef = useRef(null);

    const [isFormValid, setFormValid] = useState(false);
    const [buttonClassName, setButtonClassName] = useState('button_inactive');
    useEffect(() => {
      if (avatarError) {
        setFormValid(false);
        setButtonClassName('button_inactive');
      } else {
        setFormValid(true);
        setButtonClassName('')
      }
    }, [avatarError]);

    function clearInput() {
        setAvatar('');
        setAvatarError('Вы пропустили это поле.');
        setInputAvatarClassName('');
    }

    function handleSubmit(e) {
        e.preventDefault();
        onUpdateAvatar({
            avatar: inputRef.current.value
        });
        clearInput();
    }

    function handleClose() {
        onClose();
        clearInput();
    }

    return (
        <PopupWithForm
            name="avatar-edit"
            title="Обновить аватар"
            formName="updateAvatar"
            buttonText="Сохранить"
            isOpen={isOpen}
            onClose={handleClose}
            onSubmit={handleSubmit}
            isFormValid={isFormValid}
            buttonClassName={buttonClassName}
        >
            <label className="form__item">
                <input
                    className="form__input form__input_type_link"
                    id="avatar-link-input"
                    type="url"
                    name="avatar"
                    placeholder="Ссылка на картинку" 
                    required
                    value={avatar || ''}
                    onChange={handleAvatarChange}
                    ref={inputRef}
                />
                <span className={`form__input-error avatar-link-input-error ${inputAvatarClassName}`}>{avatarError}</span>
            </label>
        </PopupWithForm>
    )
}

export default EditAvatarPopup;
