import React, { useState, useEffect } from 'react';

function PopupWithForm({ name, title, formName, children, buttonText, isOpen, onClose, onSubmit, firstInputError, secondInputError}) {
  const [isFormValid, setFormValid] = useState(false);
  const [buttonClassName, setButtonClassName] = useState('button_inactive');

  useEffect(() => {
    if (firstInputError || secondInputError) {
      setFormValid(false);
      setButtonClassName('button_inactive');
    } else {
      setFormValid(true);
      setButtonClassName('')
    }
  }, [firstInputError, secondInputError]);


  return (
    <div className={`popup popup_type_${name} ${isOpen ? 'popup_is-opened' : ''}`}>
      <div className="popup__container">
        <h2 className="popup__title">{title}</h2>
        <form className="form" name={formName} noValidate onSubmit={onSubmit}>
          {children}
          <button className={`button button_type_submit ${!isFormValid ? 'button_inactive': ''}`} type="submit" disabled={isFormValid ? true : false}>{buttonText}</button>
        </form>
        <button className="popup__close-btn" onClick={onClose}></button>
      </div>
    </div>
  )
}

export default PopupWithForm;
