import React from "react";
import successIcon from '../images/success.svg';
import failIcon from '../images/fail.svg';

function InfoToolTip({ isOpen, isRegistrationSuccessful, onClose}) {
    return (
        <div className={`popup popup_type_sign-up ${isOpen && 'popup_is-opened'}`}>
            <div className="info-tool-tip">
                <img className="info-tool-tip__icon" src={isRegistrationSuccessful ? successIcon : failIcon} alt="Иконка успешной или неудачной регистрации" />
                <p className="info-tool-tip__text">
                    {isRegistrationSuccessful ?
                    'Вы успешно зарегистрировались!'
                    : 'Что-то пошло не так! Попробуйте еще раз'}
                </p>
                <button className="popup__close-btn" onClick={onClose}></button>
            </div>
        </div>
        )
}

export default InfoToolTip;
