import React from 'react';

let userName, userDescription, userAvatar;

function Main({ onEditAvatar, onEditProfile, onAddPlace }) {
  api.getUserData()
    .then(data => {
      userId = data._id;
      userAvatar = data.avatar;
      userName = data.name;
      userDescription = data.about;      
    })

  return (
    <main className="content">
      <section className="profile">
        <div className="profile__avatar">
          <img className="profile__avatar-image" src="" style={{ backgroundImage: `url(${userAvatar})` }} alt="фото профиля" />
          <div className="profile__darkening"></div>
          <div className="profile__avatar-edit-btn" onClick={onEditAvatar}></div>
        </div>
        <div className="profile__info">
          <div className="profile__info-user">
            <h1 className="profile__name">{userName}</h1>
            <button className="profile__edit-btn" onClick={onEditProfile}></button>
          </div>
          <p className="profile__job">{userDescription}</p>
        </div>
        <button className="profile__add-btn" onClick={onAddPlace}></button>
      </section>
      <section className="elements">
        <ul className="elements__list"></ul>
      </section>
    </main>
  )
}

export default Main;
