import React from 'react';

function PopupWithForm({ name, title, formName, children, buttonText, isOpen }) {
    return (
        <div className={`popup popup_type_${name} ${{isOpen} ? '' : 'popup_is-opened'}`}>
          <div className="popup__container">
            <h2 className="popup__title">{title}</h2>
            <form className="form" name={formName} noValidate>
              {children}
              <button className="button button_type_submit" type="submit">Сохранить</button>
            </form>
            <button className="popup__close-btn">{buttonText}</button>
          </div>
        </div>
    )
}

export default PopupWithForm;
