import React from 'react';
import { ReactDOM } from 'react';
import Header from '../components/Header';
import Main from '../components/Main';
import Footer from '../components/Footer';
import PopupWithForm from './PopupWithForm';

// const editAvatarBtn = document.querySelector('.profile__avatar-edit-btn');
// let isEditAvatarPopupOpen = false;
// editAvatarBtn.addEventListener('click', () => {
//   isEditAvatarPopupOpen = true;
// })

// const editProfileBtn = document.querySelector('.profile__edit-btn');
// let isEditProfilePopupOpen = false;
// editProfileBtn.addEventListener('click', () => {
//   isEditProfilePopupOpen = true;
// })

// const addPlaceBtn = document.querySelector('.profile__add-btn');
// let isAddPlacePopupOpen = false;
// addPlaceBtn.addEventListener('click', () => {
//   isAddPlacePopupOpen = true;
// })

function handleEditAvatarClick() {
  document.querySelector('.popup_type_avatar-edit').classList.add('popup_is-opened');
}

function handleEditProfileClick() {
  document.querySelector('.popup_type_edit-profile').classList.add('popup_is-opened');
}

function handleAddPlaceClick() {
  document.querySelector('.popup_type_add-photo').classList.add('popup_is-opened');
}

function App() {
  return (
    <body className="page">
      <div className="page__container">
        <Header className="header" />
        <Main className="content" />          
        <Footer className="footer" />

        <PopupWithForm
          name="edit-profile"
          title="Редактировать профиль"
          buttonText="Сохранить"
        >
          <>
            <label className="form__item">
              <input className="form__input form__input_type_name" id="name-input" type="text" name="name" placeholder="Ваше имя" value="Жак-Ив Кусто" minLength="2" maxLength="40" required />
              <span className="form__input-error name-input-error">Вы пропустили это поле.</span>
            </label>
            <label className="form__item">
              <input className="form__input form__input_type_job" id="job-input" type="text" name="about" placeholder="Кратко о Вас" value="Исследователь океана" minLength="2" maxLength="200" required />
              <span className="form__input-error job-input-error">Вы пропустили это поле.</span>
            </label>
          </>
        </PopupWithForm>

        {/* <div className="popup popup_type_edit-profile">
          <div className="popup__container">
            <h2 className="popup__title">Редактировать профиль</h2>
            <form className="form" name="editProfileForm" noValidate>
              <label className="form__item">
                <input className="form__input form__input_type_name" id="name-input" type="text" name="name" placeholder="Ваше имя" value="Жак-Ив Кусто" minLength="2" maxLength="40" required />
                <span className="form__input-error name-input-error">Вы пропустили это поле.</span>
              </label>
              <label className="form__item">
                <input className="form__input form__input_type_job" id="job-input" type="text" name="about" placeholder="Кратко о Вас" value="Исследователь океана" minLength="2" maxLength="200" required />
                <span className="form__input-error job-input-error">Вы пропустили это поле.</span>
              </label>
              <button className="button button_type_submit" type="submit">Сохранить</button>
            </form>
            <button className="popup__close-btn"></button>
          </div>
        </div> */}

        <div className="popup popup_type_add-photo">
          <div className="popup__container">
            <h2 className="popup__title">Новое место</h2>
            <form className="form" name="addNewPhotoForm" noValidate>
              <label className="form__item">
                <input className="form__input form__input_type_title" id="title-input" type="text" name="name" placeholder="Название" minLength="2" maxLength="30" required />
                <span className="form__input-error title-input-error">Вы пропустили это поле.</span>
              </label>
              <label className="form__item">
                <input className="form__input form__input_type_link" id="card-link-input" type="url" name="link" placeholder="Ссылка на картинку" required />
                <span className="form__input-error card-link-input-error">Введите адрес сайта.</span>
              </label>
              <button className="button button_type_submit" type="submit">Создать</button>
            </form>
            <button className="popup__close-btn"></button>
          </div>
        </div>

        <div className="popup popup_type_photo">
          <div className="popup__wrapper">
            <figure className="popup__figure">
              <img className="popup__image" src="." alt="Фотография" />
              <figcaption className="popup__caption"></figcaption>
            </figure>
            <button className="popup__close-btn"></button>
          </div>
        </div>

        <div className="popup popup_type_confirmation">
          <div className="popup__container">
            <h2 className="popup__title popup__title_place_confirmation-popup">Вы уверены?</h2>
            <form className="form" name="confirmForm" noValidate>
              <button className="button button_type_submit" type="submit">Да</button>
            </form>
            <button className="popup__close-btn"></button>
          </div>
        </div>

        <div className="popup popup_type_avatar-edit">
          <div className="popup__container">
            <h2 className="popup__title">Обновить аватар</h2>
            <form className="form" name="updateAvatar" noValidate>
              <label className="form__item">
                <input className="form__input form__input_type_link" id="avatar-link-input" type="url" name="avatar" placeholder="Ссылка на картинку" required />
                <span className="form__input-error avatar-link-input-error">Ссылка на изображение.</span>
              </label>
              <button className="button button_type_submit" type="submit">Сохранить</button>
            </form>
            <button className="popup__close-btn"></button>
          </div>
        </div>

        <template id="card">
          <li className="element">
            <img className="element__image" src="." alt="Фотография" />
            <div className="element__container">
              <h2 className="element__title"></h2>
              <div className="element__like-container">
                <button className="element__like-btn"></button>
                <span className="element__like-counter">0</span>
              </div>
            </div>
            <button className="element__delete-btn"></button>
          </li>
        </template>

      </div>
    </body>
  )
}

ReactDOM.render((
  <App />),
  document.querySelector('.page')
)

export default App;
