function handleEditAvatarClick() {
  document.querySelector('.popup_type_avatar-edit').classList.add('popup_is-opened');
}

function handleEditProfileClick() {
  document.querySelector('.popup_type_edit-profile').classList.add('popup_is-opened');
}

function handleAddPlaceClick() {
  document.querySelector('.popup_type_add-photo').classList.add('popup_is-opened');
}

function App() {
  const [isEditProfilePopupOpen, setEditProfilePopupOpen] = useState(false);
  const [isAddPhotoPopupOpen, setAddPhotoPopupOpen] = useState(false);
  const [isConfirmationPopupOpen, setConfirmationPopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = useState(false);
  const [isImagePopupOpen, setImagePopupOpen] = useState(false);

  function handleEditProfilePopupOpen() {
    setEditProfilePopupOpen(!isEditProfilePopupOpen);
  }

  function handleAddPhotoFormPopupOpen() {
    setAddPhotoPopupOpen(!isAddPhotoPopupOpen);
  }

  function handleConfirmationPopupOpen() {
    setConfirmationPopupOpen(!isConfirmationPopupOpen);
  }

  function handleEditAvatarPopupOpen() {
    setEditAvatarPopupOpen(!isEditAvatarPopupOpen);
  }

  return (
    <body className="page">
      <div className="page__container">
        <Header className="header" />
        <Main 
          className="content"
          onEditProfile={handleEditProfilePopupOpen}
          onAddPlace={handleAddPhotoFormPopupOpen}
          onEditAvatar={handleEditAvatarPopupOpen}
        />          
        <Footer className="footer" />

