import React, { useState } from 'react';
import PopupWithForm from "./PopupWithForm";

function AddPlacePopup({ isOpen, onClose, onAddPlace }) {
    const [title, setTitle] = useState('');
    function handleTitleChange(e) {
        setTitle(e.target.value);
        if(e.target.value.length < 2 || e.target.value.length > 30) {
            setTitleError('Количество символов доожно быть от 2 до 30.')
            if(!e.target.value) {
                setTitleError('Вы пропустили это поле.');
            }
        } else {
            setTitleError('');
        }
    }

    const [titleDirty, setTitleDirty] = useState(false);
    const [titleError, setTitleError] = useState('Вы пропустили это поле.');

    const [link, setLink] = useState('');
    function handleLinkChange(e) {
        setLink(e.target.value);
        const re = /^(https?:\/\/)?([\da-z\.-]+)\.([a-z\.]{2,6})([\/\w \.-]*)*\/?$/;
        if (!re.test(e.target.value)) {
            setLinkError('Введите адрес сайта.')
        }
    }

    const [LinkDirty, setLinkDirty] = useState(false);
    const [LinkError, setLinkError] = useState('Вы пропустили это поле.')

    function clearInputs() {
        setTitle('');
        setLink('');
    }

    function handleBlur(e) {
         switch (e.target.name) {
            case 'name':
                setTitleDirty(true);
                break;
            case 'link':
                setLinkDirty(true);
                break;
        }
    }

    function handleSubmit(e) {
        e.preventDefault();
        onAddPlace({
            name: title,
            link: link
        });
        clearInputs();
    }

    function handleClose() {
        onClose();
        clearInputs();
    }

    return (
        <PopupWithForm
            name="add-photo"
            title="Новое место"
            formName="addNewPhotoForm"
            buttonText="Создать"
            isOpen={isOpen}
            onClose={handleClose}
            onSubmit={handleSubmit}
        >
            <label className="form__item">
                <input
                    className="form__input form__input_type_title"
                    id="title-input"
                    type="text"
                    name="name"
                    placeholder="Название"
                    minLength="2"
                    maxLength="30"
                    required
                    value={title || ''}
                    onChange={handleTitleChange}
                    onBlur={handleBlur}
                />
                {(titleDirty && titleError) && <span className="form__input-error title-input-error">{titleError}</span>}                
            </label>
            <label className="form__item">
                <input
                    className="form__input form__input_type_link"
                    id="card-link-input"
                    type="url"
                    name="link"
                    placeholder="Ссылка на картинку"
                    required
                    value={link || ''}
                    onChange={handleLinkChange}
                    onBlur={handleBlur}
                />
                {(LinkDirty && LinkError) && <span className="form__input-error card-link-input-error">{LinkError}</span>}                
            </label>
        </PopupWithForm>
    )
}

export default AddPlacePopup;
