import React, { useState } from 'react';
import PopupWithForm from "./PopupWithForm";

function AddPlacePopup({ isOpen, onClose, onAddPlace, onValidation }) {
    const [title, setTitle] = useState('');
    const [titleError, setTitleError] = useState('');
    const [inputTitleClassName, setInputTitleClassName] = useState('');
    function handleTitleChange(e) {
        setTitle(e.target.value);
        onValidation(e.target, setTitleError, setInputTitleClassName);
        console.log(titleError, LinkError);
        if((titleError === '') || (LinkError === '') || ((titleError=== '') && (LinkError === ''))) {
            setButtonAbility(true);
            setButtonClassName('');
        } else {
            setButtonAbility(false);
            setButtonClassName('button_inactive');
        }
    }

    const [link, setLink] = useState('');
    const [LinkError, setLinkError] = useState('');
    const [inputLinkClassName, setInputLinkClassName] = useState('');
    function handleLinkChange(e) {
        setLink(e.target.value);
        onValidation(e.target, setLinkError, setInputLinkClassName);
    }

    const [ButtonAbility, setButtonAbility] = useState(false);
    const [buttonClassName, setButtonClassName] = useState('');

    // function checkValidity() {
    //     if((titleError === '') || (LinkError === '')) {
    //         setButtonAbility(true);
    //         setButtonClassName('');
    //     } else {
    //         setButtonAbility(false);
    //         setButtonClassName('button_inactive');
    //     }
    // }

    function clearInputs() {
        setTitle('');
        setTitleError('');
        setLink('');
        setLinkError('');
    }

    function handleSubmit(e) {
        e.preventDefault();
        onAddPlace({
            name: title,
            link: link
        });
        clearInputs();
    }

    function handleClose() {
        onClose();
        clearInputs();
    }

    return (
        <PopupWithForm
            name="add-photo"
            title="Новое место"
            formName="addNewPhotoForm"
            buttonText="Создать"
            isOpen={isOpen}
            onClose={handleClose}
            onSubmit={handleSubmit}
        >
            <label className="form__item">
                <input
                    className="form__input form__input_type_title"
                    id="title-input"
                    type="text"
                    name="name"
                    placeholder="Название"
                    minLength="2"
                    maxLength="30"
                    required
                    value={title || ''}
                    onChange={handleTitleChange}
                />
            <span className={`form__input-error title-input-error ${inputTitleClassName}`}>{titleError}</span>                
            </label>
            <label className="form__item">
                <input
                    className="form__input form__input_type_link"
                    id="card-link-input"
                    type="url"
                    name="link"
                    placeholder="Ссылка на картинку"
                    required
                    value={link || ''}
                    onChange={handleLinkChange}
                />
                <span className={`form__input-error card-link-input-error ${inputLinkClassName}`}>{LinkError}</span>                
            </label>
        </PopupWithForm>
    )
}

export default AddPlacePopup;
