import React, { useState } from "react";

function Register({ onRegister }) {
    const [data, setData] = useState({
        email: '',
        password: '',
    });

    function handleChange(e) {
        const {name, value} = e.target;
        setData({
            [name]: value
        })
    }

    function handleSubmit(e) {
        e.preventDefault();
        const { email, password } = data;
        onRegister(email, password);
    }

    return (
        <form classNameName="form" name="register" novalidate="" onSubmit={handleSubmit}>
            <h2 className="form__title">Регистрация</h2>
            <label className="form__item">
                <input
                    className="form__input form__input_type_email"
                    id="email-input"
                    type="email"
                    name="email"
                    placeholder="Email"
                    required=""
                    value="" 
                    onChange={handleChange}
                />
                <span className="form__input-error email-input-error">Вы пропустили это поле.</span>
            </label>
            <label className="form__item">
                <input 
                    className="form__input form__input_type_password"
                    id="password-input"
                    type="password"
                    name="password"
                    placeholder="Пароль"
                    required=""
                    value=""
                    onChange={handleChange}
                />
                <span className="form__input-error password-input-error">Вы пропустили это поле.</span>
            </label>
            <button className="button button_type_submit" type="submit">Зарегистрироваться</button>
        </form>
    )
}

export default Register;
