import React, { useState, useEffect } from 'react';
import PopupWithForm from "./PopupWithForm";

function AddPlacePopup({ isOpen, onClose, onAddPlace, onValidation }) {
    const [title, setTitle] = useState('');
    const [titleError, setTitleError] = useState('Вы пропустили это поле.');
    const [inputTitleClassName, setInputTitleClassName] = useState('');
    function handleTitleChange(e) {
        setTitle(e.target.value);
        onValidation(e.target, setTitleError, setInputTitleClassName);
    }

    const [link, setLink] = useState('');
    const [linkError, setLinkError] = useState('Вы пропустили это поле.');
    const [inputLinkClassName, setInputLinkClassName] = useState('');
    function handleLinkChange(e) {
        setLink(e.target.value);
        onValidation(e.target, setLinkError, setInputLinkClassName);
    }

    const [isFormValid, setFormValid] = useState(false);
    const [buttonClassName, setButtonClassName] = useState('button_inactive');
    useEffect(() => {
      if (titleError || linkError) {
        setFormValid(false);
        setButtonClassName('button_inactive');
      } else {
        setFormValid(true);
        setButtonClassName('')
      }
    }, [titleError, linkError]);

    function clearInputErrors() {
        setTitle('');
        setTitleError('Вы пропустили это поле.');
        setInputTitleClassName('');
        setLink('');
        setLinkError('Вы пропустили это поле.');
        setInputLinkClassName('');
    }

    function handleSubmit(e) {
        e.preventDefault();
        onAddPlace({
            name: title,
            link: link
        });
        clearInputErrors();
    }

    function handleClose() {
        onClose();
        clearInputErrors();
    }

    return (
        <PopupWithForm
            name="add-photo"
            title="Новое место"
            formName="addNewPhotoForm"
            buttonText="Создать"
            isOpen={isOpen}
            onClose={handleClose}
            onSubmit={handleSubmit}
            isFormValid={isFormValid}
            buttonClassName={buttonClassName}            
        >
            <label className="form__item">
                <input
                    className="form__input form__input_type_title"
                    id="title-input"
                    type="text"
                    name="name"
                    placeholder="Название"
                    minLength="2"
                    maxLength="30"
                    required
                    value={title || ''}
                    onChange={handleTitleChange}
                />
            <span className={`form__input-error title-input-error ${inputTitleClassName}`}>{titleError}</span>                
            </label>
            <label className="form__item">
                <input
                    className="form__input form__input_type_link"
                    id="card-link-input"
                    type="url"
                    name="link"
                    placeholder="Ссылка на картинку"
                    required
                    value={link || ''}
                    onChange={handleLinkChange}
                />
                <span className={`form__input-error card-link-input-error ${inputLinkClassName}`}>{linkError}</span>                
            </label>
        </PopupWithForm>
    )
}

export default AddPlacePopup;
